<script>
import Clients from "./components/Clients.vue";
import FAQs from "./components/FAQs.vue";
import Home from "./components/Home.vue";

//import "../../assets/css/theme.min.css";

// import 'bootstrap/dist/css/bootstrap.css'
// import 'bootstrap-vue/dist/bootstrap-vue.css'

// import "../../assets/fonts/feather/feather.css";
//import "../../assets/css/theme-dark.min.css";

export default {
  name: "App",
  components: {
    FAQs,
    Clients,
    Home
  },
  mounted: function() {
    // if(someCondition) {
    // var element = document.createElement("link");
    // element.setAttribute("rel", "stylesheet");
    // element.setAttribute("type", "text/css");
    // element.setAttribute("href", "../../assets/css/theme.min.css");
    // document.getElementsByTagName("head")[0].appendChild(element);
    // }
    this.stylesheet.light = document.querySelector("#stylesheetLight");
    this.stylesheet.dark = document.querySelector("#stylesheetDark");
    this.stylesheet.theme = localStorage.getItem("colorScheme")
      ? localStorage.getItem("colorScheme")
      : "none";

    this.loadTheme();
  },
  methods: {
    toggleColorScheme: function(colorScheme) {
      if (typeof colorScheme == "undefined") {
        if (this.stylesheet.theme == "light") {
          colorScheme = "dark";
        } else {
          colorScheme = "light";
        }
      }
      if (colorScheme == "light") {
        this.stylesheet.light.disabled = false;
        this.stylesheet.dark.disabled = true;
        colorScheme = "light";
      } else if (colorScheme == "dark") {
        this.stylesheet.light.disabled = true;
        this.stylesheet.dark.disabled = false;
        colorScheme = "dark";
      }

      localStorage.setItem("colorScheme", colorScheme);
      this.stylesheet.theme = colorScheme;
    },
    loadTheme: function() {
      var now = new Date();

      if (now.getHours() > 18 || this.stylesheet.theme == "dark") {
        this.toggleColorScheme("dark");
      } else {
        this.toggleColorScheme("light");
      }
    }
  },
  data: function() {
    return {
      stylesheet: {
        light: "",
        dark: "",
        theme: ""
      },
      menuItem: "Home",
      server: {
        LogoImageType: "png",
        LogoImgSrc:
          ""
      }
    };
  }
};
</script>

